import React, { useState } from 'react'
import "./Homepage.css"
import { Link, useLocation } from "react-router-dom";
import Peer from 'peerjs';
export let localPeerId;
function Homepage() {


  const location = useLocation()
  const path = location.pathname.split('/');
  let [audioElement, setAudioElement] = useState(new Audio());
  let [localStream, setLocalStream] = useState();
  let [k, setK] = useState(0);

  const peer = new Peer("user");
  peer.on('open', id => {
    localPeerId = id;
    console.log(localPeerId);
  });
  peer.on('call', call => {
    call.answer(localStream);
    console.log('answer');
    call.on('stream', stream => {
      console.log(stream);
      audioElement.srcObject = stream;
      audioElement.play();
    });
  });
  const handleOn = () => {
    if (k == 1) {
      audioElement.play();
      setK(0);
    } else {
      audioElement.pause();
      setK(1);
    }
  };
  return (
    <div className='containerHome'>
        <div>
            <b>RODIO</b>
            <Link to="/control"> CONTROL STATION </Link>
            <div className='vizualizer'></div>
            <button onClick={ handleOn }>ON/OFF</button>
            <button>Volume</button>
            <button>Next</button>
            <div>{path}</div>
        </div>
        <div>
            <div className='adds'></div>
            <div className='currentStation'></div>
        </div>
    </div>
  )
}

export default Homepage
